        LOG(
            (TS_MEAN(POSITIVE_JUMPS($close, $high, $low, 1), 4) + 1e-8) / 
            (TS_MEAN(NEGATIVE_JUMPS($close, $high, $low, 1), 4) + 1e-8)
        ), 
        20
    )
