LET governance_decay = TS_DECAY($governance_score, 60);
LET flow_divergence = TS_CORR($institutional_flow, $retail_flow, 20);
LET earnings_proximity = IF(ABS($days_to_earnings) <= 5, 1, 0);
LET market_uncertainty = TS_STD($market_return, 20) > QUANTILE(TS_STD($market_return, 20), 0.8, 'cross_sectional');
LET sequential_signal = RANK(governance_decay) * (-1 * RANK(flow_divergence)) * earnings_proximity * market_uncertainty;
WHERE earnings_proximity == 1 AND market_uncertainty == 1 THEN sequential_signal
